mapper.select:
SELECT A.CD_NO, A.TITLE, A.SINGER, A.MAIN_IMG, A.DETAIL_IMG, A.PRICE, A.STOCK, A.RECOMMEND_COUNT, A.WRITED_AT, COUNT(A.HT_NO),COUNT(A.NAME) FROM (SELECT C.CD_NO, C.TITLE, C.SINGER, C.MAIN_IMG, C.DETAIL_IMG, C.PRICE, C.STOCK, C.RECOMMEND_COUNT, C.WRITED_AT, H.HT_NO,H.NAME FROM CD C LEFT OUTER JOIN HASHTAG_CD TC ON C.CD_NO = TC.CD_NO LEFT OUTER JOIN HASHTAG H ON TC.HT_NO = H.HT_NO
TITLE LIKE CONCAT('%', UPPER(#{query}), '%') OR TITLE LIKE CONCAT('%', LOWER(#{query}), '%')
) A GROUP BY A.CD_NO, A.TITLE, A.SINGER, A.MAIN_IMG, A.DETAIL_IMG, A.PRICE, A.STOCK, A.RECOMMEND_COUNT, A.WRITED_AT ORDER BY CD_NO ASC LIMIT #{begin}, #{recordPerPage}

mapper.insert:
