mapper.select:
SELECT A.USER_NO, A.NAME, A.EMAIL, A.PW, A.PHONENO, A.POSTCODE, A.ROAD_ADDRESS, A.JIBUN_ADDRESS, A.DETAIL_ADDRESS, A.JOINED_AT FROM (SELECT USER_NO, NAME, EMAIL, PW, PHONENO, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, JOINED_AT FROM USERS
${column} LIKE CONCAT('%', UPPER(#{query}), '%') OR ${column} LIKE CONCAT('%', LOWER(#{query}), '%')
) A ORDER BY USER_NO DESC LIMIT #{begin}, #{recordPerPage}

SELECT A.SLEEP_USER_NO, A.NAME, A.EMAIL, A.PW, A.PHONENO, A.POSTCODE, A.ROAD_ADDRESS, A.JIBUN_ADDRESS, A.DETAIL_ADDRESS, A.JOINED_AT, A.SLEPT_AT FROM (SELECT SLEEP_USER_NO, NAME, EMAIL, PW, PHONENO, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, JOINED_AT, SLEPT_AT FROM SLEEP_USERS
${column} LIKE CONCAT('%', UPPER(#{query}), '%') OR ${column} LIKE CONCAT('%', LOWER(#{query}), '%')
) A ORDER BY SLEEP_USER_NO DESC LIMIT #{begin}, #{recordPerPage}

SELECT A.OUT_USER_NO, A.EMAIL,A.JOINED_AT, A.OUT_AT FROM (SELECT OUT_USER_NO, EMAIL, JOINED_AT, OUT_AT FROM OUT_USERS
EMAIL LIKE CONCAT('%', UPPER(#{query}), '%') OR EMAIL LIKE CONCAT('%', LOWER(#{query}), '%')
) A ORDER BY OUT_USER_NO DESC LIMIT #{begin}, #{recordPerPage}

mapper.insert:
INSERT INTO OUT_USERS (
	EMAIL
  , JOINED_AT
  , OUT_AT
  ) VALUES (
    #{email}
  , #{joinedAt}
  , NOW()
  )

mapper.select:
SELECT A.REPLY_NO, A.CONTENT, A.GROUP_NO, A.GROUP_ORDER, A.DEPTH, A.WRITE_AT, A.NAME, A.TITLE FROM (SELECT R.REPLY_NO, R.CONTENT, R.GROUP_NO, R.GROUP_ORDER, R.DEPTH, R.WRITE_AT, U.NAME, C.TITLE FROM REPLY R LEFT OUTER JOIN CD C ON R.CD_NO = C.CD_NO LEFT OUTER JOIN USERS U ON R.USER_NO = U.USER_NO
${column} LIKE CONCAT('%', UPPER(#{query}), '%') OR ${column} LIKE CONCAT('%', LOWER(#{query}), '%')
) A ORDER BY REPLY_NO ASC LIMIT #{begin}, #{recordPerPage}
