mapper.select:
	SELECT USER_NO, EMAIL, PW, PHONENO, POSTCODE, ROAD_ADDRESS, JIBUN_ADDRESS, DETAIL_ADDRESS, NAME, AGREECODE
 	FROM USERS
   WHERE EMAIL = #{email}

SELECT EMAIL
  FROM SLEEP_USERS
 WHERE EMAIL = #{email}

SELECT EMAIL
  FROM OUT_USERS
 WHERE EMAIL = #{email}

	SELECT EMAIL, PHONENO, JOINED_AT
      FROM USERS
     WHERE PHONENO = #{phoneNo}

SELECT PHONENO
  FROM SLEEP_USERS
 WHERE PHONENO = #{phoneNo}

SELECT PHONENO
  FROM OUT_USERS
 WHERE PHONENO = #{phoneNo}

mapper.insert:
INSERT INTO USERS (
     EMAIL
   , PW
   , PHONENO
   , POSTCODE
   , ROAD_ADDRESS
   , JIBUN_ADDRESS
   , DETAIL_ADDRESS
   , NAME
   , JOINED_AT
   , AGREECODE
) VALUES (
	  #{email}
	, #{pw}
	, #{phoneNo}
	, #{postcode}
	, #{roadAddress}
	, #{jibunAddress}
	, #{detailAddress}
	, #{name}
	, NOW()
	, #{agreecode}
)

mapper.update:
UPDATE USERS_ACCESS
   SET LAST_LOGIN_AT = NOW() 
 WHERE EMAIL = #{email}

UPDATE USERS
   SET AUTOLOGIN_EMAIL = #{autologinEmail}
     , AUTOLOGIN_EXPIRED_AT = #{autologinExpiredAt}
 WHERE EMAIL = #{email}

mapper.insert:
 
	  	INSERT INTO OUT_USERS (
	  		   EMAIL
	  	     , JOINED_AT
	  	     , OUT_AT
	  	   ) VALUES (
	  	       #{email}
     		 , #{joinedAt}
      	     ,NOW()
	  	   )

mapper.update:
UPDATE USERS
   SET PW = #{pw}
     , PW_MODIFIED_AT = NOW()
 WHERE EMAIL = #{email}

UPDATE USERS
   SET PHONENO = #{phoneNo}
     , POSTCODE = #{postcode}
     , ROAD_ADDRESS = #{roadAddress}
     , JIBUN_ADDRESS = #{jibunAddress}
     , DETAIL_ADDRESS = #{detailAddress}
     , NAME = #{name}
     , AGREECODE = #{agreecode}
 WHERE EMAIL = #{email}

